    def setup_test (read, write, force_mock = false)
        srand(0)
        mock_unmake() # always do this, just to be sure.  yes, it slows it down.  but... 
   
        if (force_mock or !$_REX_TEST_NO_MOCK)
            warn 'MOCKING'
            mock_make(read,write)
